import "./App.css";
import Table from "./components/Table";
import { Input, Select } from "antd";
import { SearchOutlined, LoadingOutlined } from "@ant-design/icons";
import { useState, useEffect } from "react";
import axios from "axios";

const { Option } = Select;

function App() {
  const [branches, setBranches] = useState([]);
  const [state, setState] = useState("Any");
  const [city, setCity] = useState("");
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    axios.get("/api/branches").then(({ data }) => {
      setBranches(data);
      setLoading(false);
    });
  }, []);

  console.log(branches);

  return (
    <div className="app">
      {loading ? (
        <LoadingOutlined className="loadingIcon" />
      ) : (
        <>
          <h1>Branches</h1>
          <div className="searchContainer">
            <div className="citySearch">
              <label htmlFor="state">State: </label>
              <Select
                value={state}
                className="stateSearchBar"
                onChange={setState}
              >
                <Option value="any">Any</Option>
                <Option value="anty">Anty</Option>
              </Select>

              <label htmlFor="city">City: </label>
              <Select value={city} className="citySearchBar" onChange={setCity}>
                <Option value="any">Any</Option>
                <Option value="anty">Anty</Option>
              </Select>
            </div>
            <Input
              size="large"
              placeholder="Search"
              className="searchBar"
              prefix={<SearchOutlined />}
            />
          </div>
          <Table branches={branches} />
        </>
      )}
    </div>
  );
}

export default App;
